import React, { useEffect, useState } from "react";
import './SingleProduct.css'
 import { Link } from "react-router-dom";
 import { useParams } from "react-router-dom";


function SingleProduct({productItem}){
      const{id} = useParams();
      const[product,setProduct] = useState(null);

      const fetchProduct = async()=>{
        try{
          const resp = await fetch(`http://localhost:3000/AllWomenCollection/${id}`);
          const data = await resp.json();
          setProduct(data);


        }
        catch(err){
          console.log("data is not found..!", err);
        }

      }

      useEffect(()=>{
        fetchProduct();
      },[id]);

  
    return(
        <>
        <h1>This is Single Product</h1>

        <div className="Single-Product-component">
          {product ? (
            <>
            <h1>Single Product : {productItem.id}</h1>
            <div className="single-product">
              <img src={productItem.showImage} alt={productItem.alt}/>
              <div className="single-product-detail">
                  <h2>{productItem.title}</h2>
                  <p>{productItem.price}</p>
              </div>

            </div>
            </>
          ):(
            <p className="loder">Loding...</p>
          )}
        </div>
        
        </>
        
    );
}

export default SingleProduct;
