import React, { useEffect, useState } from "react";
import './AllProduct.css';
import SingleProduct from "./SingleProduct";


function AllProduct(){
    const[isitem, setIsitem] = useState([]);

     useEffect(()=>{

        fetch("https://academics.newtonschool.co/api/v1/ecommerce/clothes/products",
        {
            method:"GET",
            headers:{
                projectId :"8spjkxc7tnxh",
                // AppType:"Soul Store",

            },

        }
        ).then((responce) => responce.json())
        .then((data) => 
        // console.log('data',data));
        setIsitem(data.data));
        // console.log('data', data);

     },[]);



    return(
      <>
     
      <div>
        {isitem && isitem.map((product) => <SingleProduct  item ={product}/>)};

        <div className="bg-white productcontainer1 col-sm-12 grid">
       
       <div className="row">
       {isitem.map((item)=>(
      <div className="card-head column col-lg-4 mt-3">
           <div className="card">
     <div id="card-container ">
         <div className="img-card-div">
           <img src={item.displayImage} alt={item.alt}/>
         </div>
        <h5 className="product-name" >{item.name}</h5>
           <div className="card-info">
           <h6 className="product-price">Price:{item.price}</h6>
           <button  className="addtocart" > add to cart </button>
           <button className="addtocart" > add to wishlist </button>
         </div>
         </div>
         </div>
         </div>
         ))}
         </div>
       
     </div> 


      </div>
       
   </>

    );
}

export default AllProduct;
