import React, { useEffect,useState } from "react";

import './WishlistPage.css';


  function WishlistPage(){
    const [favoriteProducts, setFavoriteProducts] = useState([]);

    useEffect(()=>{
        const storeFavorites = JSON.parse(localStorage.getItem('favoriteProducts'))|| [];
        setFavoriteProducts(storeFavorites);
        console.log('rendering form effect', favoriteProducts);
    },[]);

    const handleRemoveFromFavrates = (index) =>{
        let item = [...favoriteProducts];
        item.splice(index,1);

        localStorage.setItem('favoriteProducts', JSON.stringify(item));
        setFavoriteProducts(item);

    }

    return(
       <>
        <h1>This is my wishlist page...!</h1>
        
        <div className="favorite-component" >

           {favoriteProducts.length > 0 ? (
            favoriteProducts.map((product,index)=>(
                <div className='favorite-product' key={product.id}>
                <img src={product.showImage}
                    alt={product.title}
                    className="favorite-product-image" />
                <div className='favorite-product-details'>
                    <h4>{product.title}</h4>
                    <p>Price: &#8377; {product.price}</p>
                    <button onClick={() => handleRemoveFromFavrates(index)}>Remove from Favorites</button>
                </div>
            </div>

            ))
           ):(
            <p className='favorite-para'>No favorite product found.</p>
        )}

        </div>
        </>
    )

}

export default WishlistPage;
