import React from "react";
import './Description.css';
import WomenCollection from './WomenCollectionJson';
//import SingleProduct from "./SingleProduct";
// import { useParams } from "react-router-dom";
const search = window.location.search;
    const params = new URLSearchParams(search);
    const foo = params.get('id');
    console.log(foo);

console.log(WomenCollection);

var productDetail=null;
var result = WomenCollection.filter(function(element){
    if (element.id == foo){
        //console.log(element);
        productDetail=element;
        return true;
    } else {
        return false;
    }
});

//console.log(productDetail.title);

//result();


function Description(){
    // const{id} = useParams;
    
return(
        <>
         <h1>Product {productDetail.name} </h1>
        <div className="Single-productItem-compnet" title={`Buy ${productDetail.name}`}>
            <h6>{productDetail.title}</h6>
            {/* console.log("product",productItem); */}
            <span>
                <img  src={productDetail.showImage}  />
            </span>
            <div className="price-line"></div>
            <div className="product-price">{productDetail.price}</div>
            <div className="product-size">Please select a size.
            <ul>
                <li>XXS</li>
                <li>XS</li>
                <li>S</li>
                <li>M</li>
                <li>L</li>
                <li>XL</li>
                <li>XXL</li>
            </ul>

            </div>
            <label for="cars">Choose a car:</label>

            <select name="cars" id="cars">
            <option value="volvo">Volvo</option>
            <option value="saab">Saab</option>
            <option value="mercedes">Mercedes</option>
            <option value="audi">Audi</option>
            </select>

        </div>
    
        </>
    )
}

export default Description;
