       useEffect(()=>{
         fetch(`https://academics.newtonschool.co/api/v1/ecommerce/products/${id}`,{
            method:"GET",
            headers:{
              projectId:"8spjkxc7tnxh",
            },
         })
         .then(responce =>responce.json())
         .then((data) => 
            setProduct(data.data));
          
          },[id]);


  
    return(
        <>
        <h1>This is Single Product</h1>

        <div className="Single-Product-component">
          {product ?(
            <>
            <h1>Single Product : {product.id}</h1>
            <div className="single-product">
              <img src={product.showImage} alt={product.alt}/>
              <div className="single-product-detail">
                  <h2>{product.title}</h2>
                  <p>{product.price}</p>
              </div>

            </div>
            </>
          ):(
            <p className="loder">Loding...</p>
          )}
        </div>
        
        </>
        
    );
}

export default SingleProduct;
