import React, { useEffect, useState } from "react";
import './AllProduct.css';
// import SingleProduct from "./SingleProduct";
import HomePage from './HomePage';
import FooterPage from "./FooterPage";
import Slider from "./Slider";
import { Link } from "react-router-dom";
import { FaHeart, FaShoppingCart } from "react-icons/fa";
function AllProduct(){
    const[isitem, setIsitem] = useState([]);

     useEffect(()=>{
        try{
        fetch("https://academics.newtonschool.co/api/v1/ecommerce/clothes/products",
        {
            method:"GET",
            headers:{
                projectId :"8spjkxc7tnxh",
                // AppType:"Soul Store",

            },

        }
        ).then((responce) => responce.json())
        .then((data) => 
        // console.log('data'),
        setIsitem(data.data));
        //
          // console.log('data',data));
      }catch(err){
        console.log("product not fiund",err);
        
      }

     },[]);



    return(
      <>
    
       <HomePage/>
       <Slider/>
      <div>
        {/* {isitem && isitem.map((product) => <SingleProduct  item ={product}/>)}; */}

         <div className="container-fluid">
         <div className="collection-component col-sm-12 row">
       {isitem.map((item)=>
       <div className="card product-card col-sm-12 mt-5">
      <div className="card-head column col-sm-3 mt-3">
        <div className=" product-card">
       <div id="card-container" >
        <div className="img-card-div" key={item._id} >
         {/* <h1>This is single :{item._id}</h1> */}
         <Link to={`/Allproduct/${item._id}`}>
          <img src={item.displayImage} alt={item.alt}/>
           </Link>
         </div>
       
        <div  className="product-name" >{item.name}</div>
        <h6 className="product-price">Price:{item.price}</h6>
          
           <div className="card-info">
            <span  className="addtocart" title="add to Wishlist"><FaHeart/> </span>
           <span className="addtocart" title="add to Cart" > <FaShoppingCart/> </span>
         </div>
         </div>
         </div>
         </div>
         </div>
         
         )}
         </div>
       
     </div> 


      </div>
      <FooterPage/>
       
   </>

    );
}

export default AllProduct;
